import React from 'react';
import jQuery from 'jquery';
import { connect } from 'react-redux';
import { startTimer, stopTimer } from '../actions/index'


class TimeBar extends React.Component {

  width = 100;
  startInterval = null;
  points =  100;

  reduceBar() {
    let $bar = jQuery("#inside");
    this.width -= 1;
    this.points -= 10
    if (this.points <= 0) {
      this.points = 0;
    }
    if (this.width === 70) {
      $bar.css("background-color", "orange")
    } else if (this.width === 40) {
      $bar.css("background-color", "red")
    } 
    let widthPercent = this.width + "%";
    $bar.width(widthPercent);
  }





  startTimerLocal() {
    this.width = 100;
    props.startTimer();
    startInterval = setInterval(reduceBar, 200)
  }

  stopTimerLocal() {
    clearInterval(startInterval);
  }

  render() {
    return (
      <div id="progress-bar">
        <div id="inside"></div> 
        <button onClick={startTimerLocal}>Start Timer</button>
        <button onClick={stopTimerLocal}>Stop Timer</button>
      </div>
    )
  }

}

const mapStateToProps = state => {
  return {
    timerRunning: state.timerRunning
  }
}

export default connect(mapStateToProps, {
  startTimer,
  stopTimer
})(TimeBar);
