import React from 'react';
import jQuery from 'jquery';

const TimeBar = () => {
  let width = 100;

  const reduceBar = () => {
    let $bar = jQuery("#inside");
    width -= 1;
    if (width === 70) {
      $bar.css("background-color", "orange")
    } else if (width === 40) {
      $bar.css("background-color", "red")
    } 
    let widthPercent = width + "%";
    $bar.width(widthPercent);
  }

  let startInterval = setInterval(reduceBar, 200)


  return (
    <div id="progress-bar">
      <div id="inside"></div> 
    </div>

  )
}

export default TimeBar;
