import React from 'react';
import jQuery from 'jquery';
import { connect } from 'react-redux';
import { startTimer, stopTimer } from '../actions/index'


class TimeBar extends React.Component {

  width = 100;
  startInterval = null;
  points =  100;

  componentDidMount() {
    if (this.props.timerRunning) {
      this.startTimerLocal();
    }
  }

  componentDidUpdate() {
    if (this.props.timerRunning) {
      this.startTimerLocal();
    } else {
      this.stopTimerLocal();
    }
  }

  reduceBar() {
    let $bar = jQuery("#inside");
    this.width -= 1;
    this.points -= 10;
    if (this.points <= 0) {
      this.points = 0;
    }
    if (this.width === 70) {
      $bar.css("background-color", "orange")
    } else if (this.width === 40) {
      $bar.css("background-color", "red")
    } 
    let widthPercent = this.width + "%";
    $bar.width(widthPercent);
  }


  startTimerLocal() {
    this.width = 100;
    this.props.startTimer();
    this.startInterval = setInterval(this.reduceBar.bind(this), 200)
  }

  stopTimerLocal() {
    clearInterval(this.startInterval);
  }

  render() {
    return (
      <div id="progress-bar">
        <div id="inside"></div> 
        <button onClick={this.startTimerLocal.bind(this)}>Start Timer</button>
        <button onClick={this.stopTimerLocal.bind(this)}>Stop Timer</button>
      </div>
    )
  }

}

const mapStateToProps = state => {
  return {
    timerRunning: state.timerRunning
  }
}

export default connect(mapStateToProps, {
  startTimer,
  stopTimer
})(TimeBar);
