import React from 'react';
import { Paper } from '@material-ui/core';
import { connect } from 'react-redux';
import { fetchQuestion } from '../actions/index';

class QuestionContainer extends React.Component {

  handleClick() {
    this.props.fetchQuestion();
    console.log(this.props.question);
  }

  renderQuestion() {
    if (!this.props.question) {
      return <div>Loading...</div>
    } else {
      return (
        <>
          Category: {this.props.category.title}
          {this.props.question}
          <button onClick={this.handleClick}>Get Question</button>
        </>
      )
    }
  }
  render() {
    return (
      <Paper className="question-container" elevation={3}>
        {this.renderQuestion()}
      </Paper>
    );
  };
  }


const matpStateToProps = state => {
  return {
    question: state.question
  }
}

export default connect(matpStateToProps, {
  fetchQuestion
})(QuestionContainer);
