import React from 'react';
import jQuery from 'jquery';
import { connect } from 'react-redux';
import { startTimer, stopTimer } from '../actions/index'


const TimeBar = (props) => {

  let width;
  const reduceBar = () => {
    let $bar = jQuery("#inside");
    width -= 1;
    if (width === 70) {
      $bar.css("background-color", "orange")
    } else if (width === 40) {
      $bar.css("background-color", "red")
    } 
    let widthPercent = width + "%";
    $bar.width(widthPercent);
  }

  if (props.timerRunning) {
    let startInterval = setInterval(reduceBar, 200)
  }

  const startTimerLocal = () => {
    width = 100;
    props.startTimer();
  }


  return (
    <div id="progress-bar">
      <div id="inside"></div> 
      <button onClick={startTimerLocal}>Start Timer</button>
    </div>
  )
}

const mapStateToProps = state => {
  return {
    timerRunning: state.timerRunning
  }
}

export default connect(mapStateToProps, {
  startTimer,
  stopTimer
})(TimeBar);
