import React from 'react';
import { Paper } from '@material-ui/core';
import { connect } from 'react-redux';
import { fetchQuestion } from '../actions/index';

const QuestionContainer = (props) => {

  const handleClick = () => {
    props.fetchQuestion();
    console.log(props.question);
  }

  const renderQuestion = () => {
    if (!props.question) {
      return <div>Loading...</div>
    } else {
      return (
        <>
          Category: {props.category.title}
          {props.question}
          <button onClick={handleClick}>Get Question</button>
        </>
      )
    }
  }

  return (
    <Paper className="question-container" elevation={3}>
      {renderQuestion()}
    </Paper>
  );
};

const matpStateToProps = state => {
  return {
    question: state.question
  }
}

export default connect(matpStateToProps, {
  fetchQuestion
})(QuestionContainer);
